import java.io.{ByteArrayOutputStream, IOException}
import java.net.ServerSocket
import java.nio.ByteBuffer

import scala.io.Source

import org.apache.spark.{SparkConf, Logging}
import org.apache.spark.serializer.KryoSerializer
import org.apache.spark.util.IntParam

/**
 * A helper program that sends blocks of Kryo-serialized text strings out on a socket at a
 * specified rate. Used to feed data into RawInputDStream.
 *
 * Files of the required line length can be generated by this two lines in the Spark-shell
 *
 *    val text = (1 to 1000).map(x => { (1 to 15).map(_ => scala.util.Random.alphanumeric.take(6).mkString("")).mkString(" ") }).mkString("\n")
 *    org.apache.commons.io.FileUtils.writeStringToFile(new java.io.File("test.txt"), text)
 *
 */
object DynamicDataGenerator {
  def main(args: Array[String]) {
    if (args.length < 6) {
      System.err.println("Usage: RawTextSender <port> <file> <tendency> <minBytesPerSec> <maxBytesPerSec> <stepDuration> [<stepBytes>]")
      System.exit(1)
    }

    // Parse the arguments using a pattern match
    val (port, file, tendency, minBytesPerSec, maxBytesPerSec, stepDuration) = (args(0).toInt, args(1),
      args(2), args(3).toInt, args(4).toInt, args(5).toInt)

    if(tendency == "sinusoidal") {
      val stepBytes = args(6).toInt
    } else if(tendency == "step") {
    } else {
      System.err.println(s"No tendency with name $tendency")
      System.exit(1)
    }

    val blockSize = maxBytesPerSec / 10

    // Repeat the input data multiple times to fill in a buffer
    val lines = Source.fromFile(file).getLines().toArray
    val bufferStream = new ByteArrayOutputStream(blockSize + 1000)
    val ser = new KryoSerializer(new SparkConf()).newInstance()
    val serStream = ser.serializeStream(bufferStream)
    var i = 0
    while (bufferStream.size < blockSize) {
      serStream.writeObject(lines(i))
      i = (i + 1) % lines.length
    }
    val array = bufferStream.toByteArray

    val countBuf = ByteBuffer.wrap(new Array[Byte](4))
    countBuf.putInt(array.length)
    countBuf.flip()

    val serverSocket = new ServerSocket(port)
    println("Listening on port " + port)

    while (true) {
      val socket = serverSocket.accept()
      println("Got a new connection")
      var out = null
      if(tendency == "sinusoidal") {
        out = new DynamicRateLimitedOutputStream(socket.getOutputStream, tendency, minBytesPerSec, maxBytesPerSec,
          stepDuration, stepBytes)
      } else if(tendency == "step") {
        out = new DynamicRateLimitedOutputStream(socket.getOutputStream, tendency, minBytesPerSec, maxBytesPerSec,
          stepDuration)
      }

      try {
        while (true) {
          out.write(countBuf.array)
          out.write(array)
        }
      } catch {
        case e: IOException =>
          println("Client disconnected")
          socket.close()
      }
    }
  }
}

